mapper.insert:

insert into image (id, title, flag,
create_time, update_time, user_id
)
values (#{id,jdbcType=VARCHAR}, #{title,jdbcType=VARCHAR}, #{flag,jdbcType=INTEGER},
#{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{userId,jdbcType=VARCHAR}
)

mapper.select:
SELECT j.*
FROM image j where flag = 0
limit ${firstNum},${lastNum}

